package operators;

public class Shift {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 20;
		System.out.println(x<<1);
		System.out.println(x<<4);
		System.out.println(x>>1);
		System.out.println(x>>2);
		System.out.println(x>>3);
		System.out.println(x>4);

	}

}
